sys_ui_action.script:
//Client-side 'onclick' function
function addBillingHistory(){
if(g_form.getValue('u_billing_period') == ''){
//Remove any existing field message, set comments mandatory, and show a new field message
g_form.hideFieldMsg('u_billing_period');
g_form.setMandatory('u_billing_period', true);
g_form.showFieldMsg('u_billing_period','Mandatory when reopening adding history.','error');

return false; //Abort submission
}

else if(g_form.getValue('u_string_1') == ''){
//Remove any existing field message, set comments mandatory, and show a new field message
g_form.hideFieldMsg('u_string_1');
g_form.setMandatory('u_string_1', true);
g_form.showFieldMsg('u_string_1','Mandatory when reopening adding history.','error');
return false; //Abort submission
}

else if(g_form.getValue('u_billing_period'&&'u_string_1') != ''){
var answer = confirm("Add a History Record?");
    if (answer == false) {
      return false; //Abort submission
} }


//Call the UI Action and skip the 'onclick' function
gsftSubmit(null, g_form.getFormElement(), 'add_bill'); //MUST call the 'Action name' set in this UI Action
}

//Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if(typeof window == 'undefined')
billSet();

function billSet(){
//Set the 'State' to 'Active', update and reload the record
// New container to create new record
var child = new GlideRecord('x_58872_needit_ces_billing_history');

//Define field values from current record
child.ces_record = current.sys_id; 
child.billing_period = current.u_billing_period;
child.ces_billing_price = current.u_string_1;
child.requested_for = current.u_requested_for;
child.insert();

//clear field values from current record
current.u_billing_period = "";
current.u_string_1 = "";
current.update();

gs.addInfoMessage('Billing Added');
action.setRedirectURL(current);
}
